            'docker-compose':
              'version: \'3.7\'\n\nservices:\n\n  blackbox:\n    image: sixsq/blackbox:main\n    command: "$EXTRA_ARGS"\n    networks:\n     - blackbox_network\n    volumes:\n      - type: volume\n        source: blackbox_data\n        target: /opt/blackbox/data\n    environment:\n     - NUVLA_ENDPOINT\n     - NUVLA_API_KEY\n     - NUVLA_API_SECRET\n     - NUVLA_DEPLOYMENT_ID\n     - DIRECTORY=/opt/blackbox/data\n     - MQTT_HOST\n     - MQTT_POST\n     - MQTT_TOPIC\n     - RETENTION\n     - S3_CRED\n     - S3_BUCKET\n     - GPG_CRED\n    restart: always\n#    deploy:\n#      restart_policy:\n#        condition: any\n\nnetworks:\n  blackbox_network:\n    name: $NETWORK\n    external: true\n    #attachable: true\n    #driver: overlay\n    #driver: bridge\n    #  name: $NETWORK\n\nvolumes:\n  blackbox_data:\n    name: blackbox_data\n\n',
