            files: [
              {
                'file-name': 'blackbox_trigger.py',
                'file-content':
                  "#!/usr/bin/env python3\n# coding: utf-8\n\nimport os\nimport json\nimport logging\nimport argparse\n\nimport paho.mqtt.client as mqtt\n\n\nclass BlackBoxTrigger():\n\n    def __init__(self, mqtt_host, mqtt_port, mqtt_topic, objects):\n        self.objects = objects.split(',')\n        self.mqtt_host  = mqtt_host\n        self.mqtt_port  = mqtt_port\n        self.mqtt_topic = mqtt_topic\n        self.mqtt = mqtt.Client()\n        self.mqtt.enable_logger()\n        self.mqtt.on_connect = self.mqtt_on_connect\n        self.mqtt.on_message = self.mqtt_on_message\n        logging.debug(f'Params: {self.__dict__}')\n    \n    def run(self):\n        self.mqtt.connect(self.mqtt_host, self.mqtt_port)\n        self.mqtt.subscribe(self.mqtt_topic)\n        self.mqtt.loop_forever()\n\n    @staticmethod\n    def mqtt_on_connect(client, userdata, flags, rc):\n        logging.info(f'Connected with result code {rc}')\n\n    def mqtt_on_message(self, client, userdata, msg):\n        logging.debug(f'Message on topic {msg.topic}. {len(msg.payload)} bytes')\n        try:\n            message = msg.payload.decode()\n            logging.debug(f'Topic {msg.topic}: {message}')\n\n            try:\n                data = json.loads(message)\n            except:\n                logging.exception(f'Failed to parse JSON data: {data}')\n            else:\n                obj = data.get('type')\n                if obj in self.objects:\n                    self.blackbox_record(f'{obj} detected')\n        except Exception as e:\n            logging.exception('Failed to process MQTT message: ' + str(e))\n\n    def blackbox_record(self, message):\n        self.mqtt.publish('blackbox/record', message)\n    \n\ndef get_argument_parser():\n    parser = argparse.ArgumentParser(add_help=False, formatter_class=argparse.ArgumentDefaultsHelpFormatter)\n    parser.add_argument('--help',            action='help',       help='Show this help message and exit')\n    parser.add_argument('-v', '--verbose',   action='store_true', help='Verbose logging')\n    parser.add_argument('-h', '--host',      default=os.environ.get('MQTT_HOST',  '127.0.0.1'),    help='MQTT broker host')\n    parser.add_argument('-p', '--port',      default=os.environ.get('MQTT_PORT',  1883), type=int, help='MQTT broker port')\n    parser.add_argument('-t', '--topic',     default=os.environ.get('MQTT_TOPIC', '#'),            help='MQTT topic')\n    parser.add_argument('-o', '--objects',   default=os.environ.get('OBJECTS', 'person'),          help='List of objects (csv) which will trigger the generation of a BlackBox')\n    return parser\n\n\ndef main():\n    args = get_argument_parser().parse_args()\n\n    log_level = logging.DEBUG if args.verbose else logging.INFO\n    logging.basicConfig(level=log_level)\n\n    bb_trigger = BlackBoxTrigger(mqtt_host=args.host, \n                                 mqtt_port=args.port, \n                                 mqtt_topic=args.topic,\n                                 objects=args.objects)\n\n    bb_trigger.run()\n\n\nif __name__ == '__main__':\n    main()\n",
              },
            ],
            'docker-compose':
              'version: \'3.7\'\n\nservices:\n\n  object-detector:\n    image: sixsq/tensorflow-lite-object-detector:main\n    command: python3 object_detector_app.py $EXTRA_ARGS\n    networks:\n     - local_network\n     - nuvlabox_network\n    ports:\n     - 5555:5000\n    environment:\n     - INPUT_SOURCE=$VIDEO_SOURCE\n     - MQTT_BROKERS\n     - MQTT_TOPIC\n    deploy:\n      restart_policy:\n        condition: any\n  \n  mjpg2mqtt:\n    image: nuvlabox/data-collector-video\n    command: \n     - -xc\n     - ffmpeg -i "$VIDEO_SOURCE" -f mjpeg - | mqtt_pub -h data-gateway -t "video/webcam" \n    networks:\n     - local_network\n     - nuvlabox_network\n    deploy:\n      restart_policy:\n        condition: any\n\n  trigger-blackbox:\n    image: nuvlabox/mqtt-utils\n    entrypoint: python3\n    command: /blackbox_trigger.py -h data-gateway -t $MQTT_TOPIC -o $DANGEROUS_OBJECTS -v\n    networks:\n     - local_network\n     - nuvlabox_network\n    environment:\n     - MQTT_TOPIC\n     - DANGEROUS_OBJECTS\n    configs:\n      - source: blackbox_trigger.py\n        target: /blackbox_trigger.py\n    deploy:\n      restart_policy:\n        condition: any\n\nconfigs:\n  blackbox_trigger.py:\n    file: ./blackbox_trigger.py\n\nnetworks:\n  local_network:\n    name: videoanalysis-network\n    attachable: true\n    \n  nuvlabox_network:\n    name: nuvlabox-shared-network\n    external: true\n\n',
